import React, { useState, useEffect } from 'react';
import Web3 from 'web3';
import { useEthers } from "@usedapp/core";

const contractABI = [
  {
    "inputs": [],
    "name": "getMessage",
    "outputs": [
      {
        "internalType": "string",
        "name": "",
        "type": "string"
      }
    ],
    "stateMutability": "pure",
    "type": "function"
  }
];

const contractAddress = '0x4496aeACe25394FE319bd261e93B4099CF92AdA9';

function App() {
  const [web3, setWeb3] = useState(null);
  const [contract, setContract] = useState(null);
  const [message, setMessage] = useState('');
  const { activateBrowserWallet, account } = useEthers();


  const connectWallet = async () => {
    try {
      await activate(Web3.givenProvider);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  useEffect(() => {
    async function loadContract() {
      if (web3) {
        try {
          const contractInstance = new web3.eth.Contract(contractABI, contractAddress);
          setContract(contractInstance);
        } catch (error) {
          console.error('Error initializing contract:', error);
        }
      }
    }

    if (web3) {
      loadContract();
    }
  }, [web3]);

  const handleMessage = async () => {
    try {
      if (contract) {
        if (contract.methods.hasOwnProperty('getMessage')) {
          const result = await contract.methods.getMessage().call();
          setMessage(result);
        } else {
          console.error('getMessage function does not exist in contract.');
        }
      } else {
        console.error('Contract not initialized.');
      }
    } catch (error) {
      console.error('Error calling getMessage:', error);
    }
  };

  return (
    <div className="App">
       {!account && <button onClick={activateBrowserWallet}>Connect</button>}
      {/* {!account ? (
        <button onClick={connectWallet}>Connect Wallet</button>
      ) : (
        <p>Connected with address: {account}</p>
      )} */}
      <button onClick={handleMessage}>Mint</button>
      <div>
        <h1>Message:</h1> {message}
      </div>
    </div>
  );
}

export default App;
