import React, { useState, useEffect } from "react";
import Web3 from "web3";
import StudentNFTABI from "./StudentNFT.json";

const contractAddress = "0xEB61dDD0eD37E99B478943621b15933D0D6Aca9F";

function App() {
  const [web3, setWeb3] = useState(null);
  const [contract, setContract] = useState(null);
  const [message, setMessage] = useState("");
  const [contractInitialized, setContractInitialized] = useState(false);
  const [inputName, setInputName] = useState("");
  const [inputId, setInputId] = useState("");

  const calloT = async () => {
    if (window.ethereum) {
      const web3Instance = new Web3(window.ethereum);
      try {
        await window.ethereum.request({ method: "eth_requestAccounts" });
        setWeb3(web3Instance);
      } catch (error) {
        console.error("User denied account access");
      }
    }
  };

  useEffect(() => {
    async function loadContract() {
      if (web3) {
        try {
          const contractInstance = new web3.eth.Contract(
            StudentNFTABI,
            contractAddress
          );
          setContract(contractInstance);
          setContractInitialized(true);
          contractInstance.events
            .StudentMinted()
            .on("data", (event) => {
              const { tokenId, name, id } = event.returnValues;
              setMessage(
                `Student NFT Minted - Name: ${name}, ID: ${id}, Token ID: ${tokenId}`
              );
            })
            .on("error", (error) => {
              console.error("Error listening to StudentMinted event:", error);
            });
        } catch (error) {
          console.error("Error initializing contract:", error);
        }
      }
    }

    if (web3) {
      loadContract();
    }
  }, [web3]);

  const handleMessage = async () => {
    try {
      if (contract) {
        const tokenId = 0;
        console.log("Calling getStudentDetails with tokenId:", tokenId);
        const result = await contract.methods
          .getStudentDetails(tokenId)
          .call({ gas: 3000000 });
        console.log("Result from contract call:", result);

        if (result) {
          const [name, id] = result;
          setMessage(`Name: ${name}, ID: ${id}`);
        } else {
          console.error("No result returned from the contract.");
        }
      } else {
        console.error("Contract not initialized.");
      }
    } catch (error) {
      console.error("Error calling getStudentDetails:", error);
    }
  };

  const mintStudent = async () => {
    try {
      if (contractInitialized) {
        const accounts = await web3.eth.getAccounts();
        if (accounts.length > 0) {
          const name = inputName;
          const id = parseInt(inputId);
          await contract.methods
            .mintStudent(name, id)
            .send({ from: accounts[0] });
          setMessage("Student NFT minted successfully.");
        } else {
          console.error("No Ethereum accounts available.");
        }
      } else {
        console.error("Contract not initialized.");
      }
    } catch (error) {
      console.error("Error minting student NFT:", error);
    }
  };

  return (
    <div className="App">
      <button onClick={calloT}>Connect Wallet</button>
      <div>
        <label>
          Name:
          <input
            type="text"
            value={inputName}
            onChange={(e) => setInputName(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          ID:
          <input
            type="number"
            value={inputId}
            onChange={(e) => setInputId(e.target.value)}
          />
        </label>
      </div>
      <button onClick={mintStudent} disabled={!contractInitialized}>
        Mint Student NFT
      </button>
      <button onClick={handleMessage}>Mint</button>
      <div>
        <h1>Message:</h1> {message}
      </div>
    </div>
  );
}

export default App;
