import React, { useState, useEffect } from "react";
import Web3 from "web3";
import StudentNFTABI from "./StudentNFT.json";

const contractAddress = "0xD7ACd2a9FD159E69Bb102A1ca21C9a3e3A5F771B";

function App() {
  const [web3, setWeb3] = useState(null);
  const [contract, setContract] = useState(null);
  const [message, setMessage] = useState("");
  const [contractInitialized, setContractInitialized] = useState(false);
  const [inputName, setInputName] = useState(""); // State for user input name
  const [inputId, setInputId] = useState("");
  const calloT = async () => {
    if (window.ethereum) {
      const web3Instance = new Web3(window.ethereum);
      try {
        await window.ethereum.request({ method: "eth_requestAccounts" });
        setWeb3(web3Instance);
      } catch (error) {
        console.error("User denied account access");
      }
    }
  };

  useEffect(() => {
    async function loadContract() {
      if (web3) {
        try {
          const contractInstance = new web3.eth.Contract(
            StudentNFTABI,
            contractAddress
          );
          setContract(contractInstance);
          setContractInitialized(true);
        } catch (error) {
          console.error("Error initializing contract:", error);
        }
      }
    }

    if (web3) {
      loadContract();
    }
  }, [web3]);

  const mintStudent = async () => {
    try {
      if (contractInitialized) {
        const accounts = await web3.eth.getAccounts();
        if (accounts.length > 0) {
          const name = inputName;
          const id = parseInt(inputId);
          await contract.methods
            .mintStudent(name, id)
            .send({ from: accounts[0] });
          setMessage("Student NFT minted successfully.");
        } else {
          console.error("No Ethereum accounts available.");
        }
      } else {
        console.error("Contract not initialized.");
      }
    } catch (error) {
      console.error("Error minting student NFT:", error);
    }
  };

  return (
    <div className="App">
      <button onClick={calloT}>Connect Wallet</button>
      <button onClick={mintStudent} disabled={!contractInitialized}>
        Mint Student NFT
      </button>
      <div>
        <h1>Message:</h1> {message}
      </div>
    </div>
  );
}

export default App;
