import React, { useState, useEffect } from 'react';
import Web3 from 'web3';

const contractABI = [
  {
  "inputs": [],
  "name": "getMessage",
  "outputs": [
    {
      "internalType": "string",
      "name": "",
      "type": "string"
    }
  ],
  "stateMutability": "pure",
  "type": "function"
}

];

const contractAddress = '0x4496aeACe25394FE319bd261e93B4099CF92AdA9'; 

function App() {
  const [web3, setWeb3] = useState(null);
  const [contract, setContract] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    async function connectToWeb3() {
      if (window.ethereum) {
        const web3Instance = new Web3(window.ethereum);
        try {
          await window.ethereum.request({ method: 'eth_requestAccounts' });
          setWeb3(web3Instance);
        } catch (error) {
          console.error('User denied account access');
        }
      }
    }
    connectToWeb3();
  }, []);

  useEffect(() => {
    async function loadContract() {
      if (web3) {
        try {
          const contractInstance = new web3.eth.Contract(contractABI, contractAddress);
          setContract(contractInstance);
        } catch (error) {
          console.error('Error initializing contract:', error);
        }
      }
    }

    if (web3) {
      loadContract();
    }
  }, [web3]);



  const handleGetMessage = async () => {
    try {
      if (contract) {
        console.log('Contract methods:', contract.methods);
        if (contract.methods.hasOwnProperty('getMessage')) {
          console.log('getMessage function exists in contract.');
          const result = await contract.methods.getMessage().call();
          setMessage(result);
        } else {
          console.error('getMessage function does not exist in contract.');
        }
      } else {
        console.error('Contract not initialized.');
      }
    } catch (error) {
      console.error('Error calling getMessage:', error);
    }
  };




  return (
    <div className="App">
      <button onClick={handleGetMessage}>Get Message from Contract</button>
      <div>
        <strong>Message from Contract:</strong> {message}
      </div>
    </div>
  );
}

export default App;
