import React, { useState } from "react";
import "./App.css";
import { ethers } from "ethers";
import StudentABI from "./StudentABI.json";

function App() {
  const [provider, setProvider] = useState(null);
  const [contract, setContract] = useState(null);
  const [studentId, setStudentId] = useState("");
  const [newStudentId, setNewStudentId] = useState("");
  const [newStudentName, setNewStudentName] = useState("");
  const [message, setMessage] = useState("");

  const contractAddress = "0x657740c9DcE5E1CB307d3ebCdfd6048d8b67F0B2";
  const contractABI = StudentABI;

  async function connectToEthereum() {
    try {
      if (window.ethereum) {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        await window.ethereum.request({ method: "eth_requestAccounts" });
        setProvider(provider);

        const contract = new ethers.Contract(
          contractAddress,
          contractABI,
          provider.getSigner()
        );
        setContract(contract);
      } else {
        alert("Please install MetaMask or use a Web3-enabled browser.");
      }
    } catch (error) {
      alert("Error connecting to Ethereum:", error);
    }
  }

  async function getStudent() {
    try {
      if (contract) {
        const result = await contract.getStudent(studentId);
        setMessage(`Student ID: ${result[0]}, Name: ${result[1]}`);
      } else {
        alert("Contract not initialized.");
      }
    } catch (error) {
      alert("Error getting student:", error);
    }
  }

  async function addStudent() {
    try {
      if (contract) {
        const tx = await contract.addStudent(newStudentId, newStudentName);
        await tx.wait();
        setMessage("Student NFT minted successfully.");
      } else {
        alert("Contract not initialized.");
      }
    } catch (error) {
      alert("Error adding student:", error);
    }
  }

  return (
    <div className="App">
      <h1>Student Registry</h1>
      <button onClick={connectToEthereum}>Connect to Ethereum</button>

      <hr />
      <div>
        <label>Student ID: </label>
        <input
          type="number"
          value={newStudentId}
          onChange={(e) => setNewStudentId(e.target.value)}
        />
        <label>Student Name: </label>
        <input
          type="text"
          value={newStudentName}
          onChange={(e) => setNewStudentName(e.target.value)}
        />
        <button onClick={addStudent}>Add Student</button>
      </div>
      <hr />
      <div>
        <label>Enter Student ID: </label>
        <input
          type="number"
          value={studentId}
          onChange={(e) => setStudentId(e.target.value)}
        />
        <button onClick={getStudent}>Get Student</button>
      </div>
      <div>
        <p>{message}</p>
      </div>
    </div>
  );
}

export default App;
