import React, { useState, useEffect } from 'react';
import Web3 from 'web3';

const contractABI = [

];

const contractAddress = '0xD7ACd2a9FD159E69Bb102A1ca21C9a3e3A5F771B'; 

function App() {
  const [web3, setWeb3] = useState(null);
  const [contract, setContract] = useState(null);
  const [message, setMessage] = useState('');
  const [newMessage, setNewMessage] = useState('');



  const calloT = async () => {
    if (window.ethereum) {
      const web3Instance = new Web3(window.ethereum);
      try {
        await window.ethereum.request({ method: 'eth_requestAccounts' });
        setWeb3(web3Instance);
      } catch (error) {
        console.error('User denied account access');
      }
    }
  };

  useEffect(() => {
    async function loadC() {
      if (web3) {
        try {
          const contractInstance = new web3.eth.Contract(contractABI, contractAddress);
          setContract(contractInstance);
        } catch (error) {
          console.error('Error initializing contract:', error);
        }
      }
    }

    if (web3) {
      loadC();
    }
  }, [web3]);


  const handleSetMessage = async () => {
    try {
      if (contract) {
        if (contract.methods.hasOwnProperty('mintStudent')) {
          await contract.methods.setMessage(newMessage).send({ from: account });
          console.log('Message set successfully');
        } else {
          console.error('mintStudent function does not exist in contract.');
        }
      } else {
        console.error('Contract not initialized.');
      }
    } catch (error) {
      console.error('Error calling setMessage:', error);
    }
  };

  const handleMessage = async () => {
    try {
      if (contract) {
        if (contract.methods.hasOwnProperty('getStudentDetails')) {
          const result = await contract.methods.getStudentDetails().call();
          setMessage(result);
        } else {
          console.error('getStudentDetails function does not exist in contract.');
        }
      } else {
        console.error('Contract not initialized.');
      }
    } catch (error) {
      console.error('Error calling getMessage:', error);
    }
  };




  return (
    <div className="App">
      <button onClick={calloT}>Conect-Wallet</button>
      <button onClick={handleMessage}>Mint</button>
      <div>
        <h1>Message:</h1> {message}
      </div>
      <div>
        <input
          type="text"
          placeholder="New message"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button onClick={handleSetMessage}>Set Message</button>
      </div>
    </div>

  );
}

export default App;
