        await window.ethereum.request({ method: 'eth_requestAccounts' });
        setWeb3(web3Instance);
      } catch (error) {
        console.error('User denied account access');
      }
    }
  };

  useEffect(() => {
    async function loadC() {
      if (web3) {
        try {
          const contract = new web3.eth.Contract(contractABI, contractAddress);
          setContract(contractInstance);
        } catch (error) {
          console.error('Error initializing contract:', error);
        }
      }
    }

    if (web3) {
      loadC();
    }
  }, [web3]);


  const handleSetMessage = async () => {
    try {
      if (contract) {
        if (contract.methods.hasOwnProperty('mintStudent')) {
          // Ensure you have an account and it's the owner or has necessary permissions
          if (!account) {
            console.error('No Ethereum account available.');
            return;
          }

          await contract.methods.mintStudent(newStudentName, newStudentId).send({ from: account });
          console.log('Student minted successfully');
        } else {
          console.error('mintStudent function does not exist in contract.');
        }
      } else {
        console.error('Contract not initialized.');
      }
    } catch (error) {
      console.error('Error minting student:', error);
    }
  };

  const handleMessage = async () => {
    try {
      if (contract) {
        if (contract.methods.hasOwnProperty('getStudentDetails')) {
          const result = await contract.methods.getStudentDetails().call();
          setMessage(result);
        } else {
          console.error('getStudentDetails function does not exist in contract.');
        }
      } else {
        console.error('Contract not initialized.');
      }
    } catch (error) {
      console.error('Error calling getMessage:', error);
    }
  };




  return (
    <div className="App">
      {!account ? (
        <button onClick={calloT}>Connect Wallet</button>
      ) : (
        <p>Connected with address: {account}</p>
      )}
      <div>
        <input
          type="text"
          placeholder="Student Name"
          value={newStudentName}
          onChange={(e) => setNewStudentName(e.target.value)}
        />
        <input
          type="number"
          placeholder="Student ID"
          value={newStudentId}
          onChange={(e) => setNewStudentId(e.target.value)}
        />
      </div>
      <button onClick={handleSetMessage}>Mint Student</button>
    </div>
  );
}

export default App;
